pub enum MatrixComparisonResult<T, C, E> where T: Copy,
         C: ElementwiseComparator<T, E>, E: ComparisonFailure {
    Match,
    MismatchedDimensions {
        dim_x: (usize, usize),
        dim_y: (usize, usize),
    },
    MismatchedElements {
        comparator: C,
        mismatches: Vec<MatrixElementComparisonFailure<T, E>>,
    },
}

pub enum VectorComparisonResult<T, C, E> where T: Copy,
         C: ElementwiseComparator<T, E>, E: ComparisonFailure {
    Match,
    MismatchedDimensions {
        dim_x: usize,
        dim_y: usize,
    },
    MismatchedElements {
        comparator: C,
        mismatches: Vec<VectorElementComparisonFailure<T, E>>,
    },
}

pub enum UlpComparisonResult {
    ExactMatch,
    Difference(u64),
    IncompatibleSigns,
    Nan,
}
